fun assignNextUniqueUnicode(word: String): Int? {
    // Primero, verifica si la palabra ya tiene un mapeo para evitar duplicados
    if (wordToUnicodePoint.containsKey(word)) {
        println("Advertencia: La palabra '$word' ya tiene un Unicode asignado: U+${wordToUnicodePoint[word]?.toString(16)?.uppercase()}")
        return null // O podrÃ­as retornar el existente si ese es el comportamiento deseado
    }

    // Asegurarse de no exceder el lÃ­mite del PUA (U+FFFFF para el SPUA)
    if (nextAvailableUnicodePoint > 0xFFFFF) { // Si usas SIPA, serÃ­a > 0x10FFFF
        println("Error: Se ha agotado el rango de caracteres PUA disponibles en este bloque.")
        // PodrÃ­as aquÃ­ cambiar a otro bloque PUA o manejar el error de otra forma
        return null
    }

    // Asigna el punto de cÃ³digo actual a la palabra
    val currentUnicodePoint = nextAvailableUnicodePoint
    wordToUnicodePoint[word] = currentUnicodePoint
    unicodePointToWord[currentUnicodePoint] = word

