    nextAvailableUnicodePoint++

    // Opcional: Imprime para seguimiento
    val unicodeCharRepresentation = String(Character.toChars(currentUnicodePoint))
    println("Mapeada: '$word' -> '$unicodeCharRepresentation' (U+${currentUnicodePoint.toString(16).uppercase()})")

    return currentUnicodePoint
}

