    alImportarBd: (Uri, Boolean) -> Unit, // Callback para importar (URI del archivo, habilitar por defecto)
    alCancelar: () -> Unit, // Callback para cancelar
    modificador: Modifier = Modifier
) {
    // Estado para la URI del archivo seleccionado
    var uriArchivoSeleccionado by remember { mutableStateOf<Uri?>(null) } // Variable hispanizada
    // Estado para el switch de habilitar por defecto
    var habilitarPorDefecto by remember { mutableStateOf(true) } // Variable hispanizada

    // Launcher para abrir el selector de archivos (SAF)
    val selectorArchivoLauncher = rememberLauncherForActivityResult(
        contract = ActivityResultContracts.OpenDocument(), // Contrato del framework en inglÃ©s
        onResult = { uri -> // ParÃ¡metro del resultado en inglÃ©s
            uriArchivoSeleccionado = uri // Asigna la URI a la variable de estado hispanizada
            // AquÃ­ podrÃ­as aÃ±adir lÃ³gica para pre-leer el nombre del archivo o metadatos del ZIM
        }
    )

    Scaffold(
        topBar = {
            TopAppBar(title = { Text(stringResource(R.string.titulo_importar_bd)) }) // Recurso hispanizado
        }
    ) { rellenoInterior -> // ParÃ¡metro hispanizado
        Column(
            modifier = modificador
                .padding(rellenoInterior) // Uso de parÃ¡metro hispanizado
                .padding(16.dp)
                .fillMaxWidth()
        ) {
            Button(onClick = {
                // Abrir selector de archivos, filtrando por tipos de base de datos
                selectorArchivoLauncher.launch(arrayOf("application/x-sqlite3", "application/x-zim")) // Tipos MIME para SQLite y ZIM
            }) {
                Text(stringResource(R.string.boton_seleccionar_archivo)) // Recurso hispanizado
            }

            Spacer(modifier = Modifier.height(16.dp))

            // Mostrar el archivo seleccionado
            uriArchivoSeleccionado?.let { uri -> // Variable hispanizada, let de Kotlin
                Text(text = stringResource(R.string.archivo_seleccionado, uri.lastPathSegment ?: uri.toString())) // Recurso hispanizado
                Spacer(modifier = Modifier.height(16.dp))
                Row(verticalAlignment = Alignment.CenterVertically) { // AlineaciÃ³n de Compose en inglÃ©s
                    Text(text = stringResource(R.string.habilitar_al_importar)) // Recurso hispanizado
                    Spacer(modifier = Modifier.weight(1f)) // Espaciador de Compose en inglÃ©s
                    Switch(
                        checked = habilitarPorDefecto, // Variable hispanizada
                        onCheckedChange = { habilitarPorDefecto = it } // Actualiza la variable hispanizada
                    )
                }
                Spacer(modifier = Modifier.height(16.dp))
                Button(
                    onClick = { alImportarBd(uri, habilitarPorDefecto) }, // Callback y variables hispanizadas
                    modifier = Modifier.fillMaxWidth()
                ) {
                    Text(stringResource(R.string.boton_importar)) // Recurso hispanizado
                }
            } ?: run {
                // Mensaje si no se ha seleccionado ningÃºn archivo
                Text(stringResource(R.string.no_hay_archivo_seleccionado)) // Recurso hispanizado
            }

            Spacer(modifier = Modifier.height(16.dp))
            Button(
                onClick = alCancelar, // Callback hispanizado
                modifier = Modifier.fillMaxWidth()
            ) {
                Text(stringResource(R.string.boton_cancelar)) // Recurso hispanizado
            }
        }
    }
}
