// Modelo de vista para la pantalla principal de bÃºsqueda
class ModeloVistaPrincipal(private val repositorioDiccionario: RepositorioDiccionario) : ViewModel() { // Nombre de clase y parÃ¡metro hispanizados

    // Estado del texto que el usuario escribe en la barra de bÃºsqueda
    private val _textoBusqueda = MutableStateFlow("") // Variable hispanizada
    val textoBusqueda: StateFlow<String> = _textoBusqueda.asStateFlow() // Variable y funciÃ³n hispanizadas

    // Flujo de los resultados de la bÃºsqueda, que se actualiza reactivamente
    val resultadosBusqueda: StateFlow<List<TerminoKotlin>> = textoBusqueda // Variable hispanizada
        .flatMapLatest { consulta -> // ParÃ¡metro hispanizado
            // Si la consulta estÃ¡ vacÃ­a, muestra todos los tÃ©rminos; de lo contrario, busca
            if (consulta.isBlank()) {
                repositorioDiccionario.obtenerTodosLosTerminos() // FunciÃ³n hispanizada
            } else {
                repositorioDiccionario.buscarTerminos(consulta) // FunciÃ³n hispanizada
            }
            // NOTA: AquÃ­ se aÃ±adirÃ­a la combinaciÃ³n con los flujos de las bases de datos personalizadas.
            // Por simplicidad en este ejemplo, solo estamos usando la DB oficial.
        }
        .stateIn(
            viewModelScope, // Scope del ViewModel en inglÃ©s
            started = SharingStarted.WhileSubscribed(5000), // Propiedades de Flow en inglÃ©s
            initialValue = emptyList() // Valor inicial en inglÃ©s
        )

    // FunciÃ³n para actualizar el texto de bÃºsqueda desde la UI
    fun actualizarTextoBusqueda(nuevoTexto: String) { // Nombre de funciÃ³n y parÃ¡metro hispanizados
        _textoBusqueda.value = nuevoTexto // Variable hispanizada
    }

    // --- Futuras funciones del ViewModel ---
    // fun agregarTerminoAFavoritos(idTermino: Int) { ... }
    // fun obtenerSugerenciasParaCompletado(consulta: String) { ... }
    // fun obtenerTerminoAleatorio() { ... }

    // Clase FÃ¡brica para instanciar ModeloVistaPrincipal con dependencias
    class Fabrica(private val aplicacion: AplicacionDiccionario) : ViewModelProvider.Factory { // Nombre de clase y parÃ¡metro hispanizados
        @Suppress("UNCHECKED_CAST")
        override fun <T : ViewModel> create(modelClass: Class<T>): T { // MÃ©todo del framework en inglÃ©s
            // Asegura que estamos creando una instancia de ModeloVistaPrincipal
            if (modelClass.isAssignableFrom(ModeloVistaPrincipal::class.java)) { // Clase hispanizada
                // Devuelve una nueva instancia de ModeloVistaPrincipal
                return ModeloVistaPrincipal(aplicacion.repositorioDiccionario) as T // Clase y variable hispanizadas
            }
            // Lanza una excepciÃ³n si se intenta crear un ViewModel de tipo desconocido
            throw IllegalArgumentException("Clase de ModeloVista desconocida para ModeloVistaPrincipal") // ExcepciÃ³n en inglÃ©s, mensaje hispanizado
        }
    }
}

