import android.content.Context
import android.net.Uri
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext
import java.io.OutputStreamWriter

// Dentro de MainViewModel.kt o un nuevo FileManagementViewModel.kt
fun saveDefinitionToFile(uri: Uri, content: String, context: Context) {
    viewModelScope.launch {
        withContext(Dispatchers.IO) { // Ejecutar en un hilo de I/O
            try {
                context.contentResolver.openOutputStream(uri)?.use { outputStream ->
                    OutputStreamWriter(outputStream).use { writer ->
                        writer.write(content)
                        writer.flush()
                    }
                }
                // Opcional: Mostrar un Toast o Snackbar de Ã©xito
            } catch (e: Exception) {
                // Manejar error: loguear, mostrar Toast de error, etc.
                e.printStackTrace()
            }
        }
    }
}
