    val searchQuery by viewModel.searchQuery.collectAsState()
    val searchResults by viewModel.searchResults.collectAsState()

    // Scaffold proporciona la estructura bÃ¡sica de Material Design (AppBar, FloatingActionButton, etc.)
    Scaffold(
        topBar = {
            TopAppBar(title = { Text("Diccionario Kotlin") }) // Barra superior con el tÃ­tulo de la app
        }
