package com.tudominio.diccionariokotlin.ui

import androidx.lifecycle.ViewModel
import androidx.lifecycle.ViewModelProvider
import androidx.lifecycle.viewModelScope
import com.tudominio.diccionariokotlin.data.TerminoKotlin
import com.tudominio.diccionariokotlin.data.TerminoKotlinDao
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.flow.collectLatest
import kotlinx.coroutines.launch

class MainViewModel(private val terminoDao: TerminoKotlinDao) : ViewModel() {

    private val _searchQuery = MutableStateFlow("")
    val searchQuery: StateFlow<String> = _searchQuery.asStateFlow()

    private val _searchResults = MutableStateFlow<List<TerminoKotlin>>(emptyList())
    val searchResults: StateFlow<List<TerminoKotlin>> = _searchResults.asStateFlow()

    init {
        viewModelScope.launch {
            searchQuery.collectLatest { query ->
                performSearch(query)
            }
        }
    }

    fun onSearchQueryChanged(query: String) {
        _searchQuery.value = query
    }

    private fun performSearch(query: String) {
        viewModelScope.launch {
            if (query.isBlank()) {
                terminoDao.getAllTerminos().collectLatest {
                    _searchResults.value = it
                }
            } else {
                val formattedQuery = "%${query.trim()}%"
                terminoDao.searchTerminos(formattedQuery).collectLatest {
                    _searchResults.value = it
