import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.flow.collectLatest
import kotlinx.coroutines.launch

// ViewModel para la pantalla principal del diccionario
class MainViewModel(private val terminoDao: TerminoKotlinDao) : ViewModel() {

    // MutableStateFlow para el tÃ©rmino de bÃºsqueda, inicializado vacÃ­o
    private val _searchQuery = MutableStateFlow("")
    val searchQuery: StateFlow<String> = _searchQuery.asStateFlow()

    // MutableStateFlow para los resultados de la bÃºsqueda, inicialmente vacÃ­os
    private val _searchResults = MutableStateFlow<List<TerminoKotlin>>(emptyList())
    val searchResults: StateFlow<List<TerminoKotlin>> = _searchResults.asStateFlow()

    init {
        // Observa los cambios en searchQuery y realiza la bÃºsqueda cada vez que cambia
        viewModelScope.launch {
            searchQuery.collectLatest { query ->
                performSearch(query)
            }
        }
    }

    // Actualiza el tÃ©rmino de bÃºsqueda
    fun onSearchQueryChanged(query: String) {
        _searchQuery.value = query
    }

    // Realiza la bÃºsqueda en la base de datos
    private fun performSearch(query: String) {
        viewModelScope.launch {
            // Si la consulta estÃ¡ vacÃ­a, muestra todos los tÃ©rminos
            // De lo contrario, realiza una bÃºsqueda LIKE
            if (query.isBlank()) {
                terminoDao.getAllTerminos().collectLatest {
                    _searchResults.value = it
                }
            } else {
                // Prepara la consulta para que coincida en cualquier parte del tÃ©rmino o definiciÃ³n
                val formattedQuery = "%${query.trim()}%"
                terminoDao.searchTerminos(formattedQuery).collectLatest {
                    _searchResults.value = it
                }
            }
        }
    }
}

// FactorÃ­a para crear el ViewModel con el DAO inyectado
class MainViewModelFactory(private val terminoDao: TerminoKotlinDao) : ViewModelProvider.Factory {
    override fun <T : ViewModel> create(modelClass: Class<T>): T {
        if (modelClass.isAssignableFrom(MainViewModel::class.java)) {
            @Suppress("UNCHECKED_CAST")
            return MainViewModel(terminoDao) as T
        }
        throw IllegalArgumentException("Unknown ViewModel class")
    }
}
